<!DOCTYPE html>
<html lang="en">
<head>
	<meta charset="UTF-8">
	<meta name="viewport" content="width=device-width, initial-scale=1.0">
	<title>Document</title>
</head>
<body>
<div class="container mt-5 pt-5">
        <div class="row">
            <h3 class="text-center">Nos actualit√©s</h3>
            <hr>
            <div class="wrapper20">
                <i id="left" class="fa-solid fa-angle-left ii"></i>
                <ul class="carousel20">

                    <li class="card-service wow fadeInUp card20 ">
                        <img src="data:image/jpeg;base64," class="card-img-top"
                            alt="...">

                    </li>
                    <li class="card-service wow fadeInUp card20 ">
                        <img src="data:image/jpeg;base64," class="card-img-top"
                            alt="...">

                    </li>
                    <li class="card-service wow fadeInUp card20 ">
                        <img src="data:image/jpeg;base64," class="card-img-top"
                            alt="...">

                    </li>
                   



                </ul>
                <i id="right" class="fa-solid fa-angle-right ii"></i>
            </div>
        </div>
    </div>

	<style>
   .wrapper20 {
 
 width: 100%;
 position: relative;
}

.wrapper20 .ii {
 top: 50%;
 height: 50px;
 width: 50px;
 cursor: pointer;
 font-size: 1.25rem;
 position: absolute;
 text-align: center;
 line-height: 50px;
 background: #fff;
 border-radius: 50%;
 box-shadow: 0 3px 6px rgba(0, 0, 0, 0.23);
 transform: translateY(-50%);
 transition: transform 0.1s linear;
 z-index: 1;
}

.wrapper20 .ii:active {
 transform: translateY(-50%) scale(0.85);
}

.wrapper20 .ii:first-child {
 left: -22px;
}



.wrapper20 .ii:last-child {
 right: -22px;
}



.wrapper20 .carousel20 {
 display: grid;
 grid-auto-flow: column;
 grid-auto-columns: calc((100% / 3) - 12px);
 overflow-x: auto;
 scroll-snap-type: x mandatory;
 gap: 16px;
/*  border-radius: 8px; */
 scroll-behavior: smooth;
 scrollbar-width: none;
}



.carousel20::-webkit-scrollbar {
 display: none;
}



.carousel20.no-transition {
 scroll-behavior: auto;
}



.carousel20.dragging {
 scroll-snap-type: none;
 scroll-behavior: auto;
}



.carousel20.dragging .card {
 cursor: grab;
 user-select: none;
}



.carousel20 :where(.card, .img) {
 display: flex;
 justify-content: center;
 align-items: center;
}



.carousel20 .card20 {
 scroll-snap-align: start;
/*  height: 342px; */
 list-style: none;
 background: #fff;
 cursor: pointer;
 /* padding-bottom: 15px; */
 flex-direction: column;
/*  border-radius: 8px; */
}



.carousel20 .card20 .img {
 background: #8b53ff;
 height: 148px;
 width: 148px;
 border-radius: 50%;
}



.card20 .img img {
 width: 140px;
 height: 140px;
 border-radius: 50%;
 object-fit: cover;
 border: 4px solid #fff;
}



.carousel20 .card20 h2 {
 font-weight: 500;
 font-size: 1.56rem;
 margin: 30px 0 5px;
}



.carousel20 .card20 span {
 color: #6a6d78;
 font-size: 1.31rem;
}
@media screen and (max-width: 900px) {
  .wrapper20 .carousel20 {
    grid-auto-columns: calc((100% / 2) - 9px);
  }

}



@media screen and (max-width: 600px) {
  .wrapper20 .carousel20 {
    grid-auto-columns: 100%;
  }
}


@media (max-width:760PX) {
  .plan {
    background-image: url('concert.jpg');
    background-size: cover;
    background-repeat: no-repeat;
    height: 400px;
  }
}

</style>
<script>
const wrapper = document.querySelector(".wrapper20");
const carousel = document.querySelector(".carousel20");
const firstCardWidth = carousel.querySelector(".card20").offsetWidth;
const arrowBtns = document.querySelectorAll(".wrapper20 i");
const carouselChildrens = [...carousel.children];

let isDragging = false,
	isAutoPlay = true,
	startX,
	startScrollLeft,
	timeoutId;


let cardPerView = Math.round(carousel.offsetWidth / firstCardWidth);


carouselChildrens
	.slice(-cardPerView)
	.reverse()
	.forEach((card) => {
		carousel.insertAdjacentHTML("afterbegin", card.outerHTML);
	});


carouselChildrens.slice(0, cardPerView).forEach((card) => {
	carousel.insertAdjacentHTML("beforeend", card.outerHTML);
});


carousel.classList.add("no-transition");
carousel.scrollLeft = carousel.offsetWidth;
carousel.classList.remove("no-transition");


arrowBtns.forEach((btn) => {
	btn.addEventListener("click", () => {
		carousel.scrollLeft += btn.id == "left" ? -firstCardWidth : firstCardWidth;
	});
});

const dragStart = (e) => {
	isDragging = true;
	carousel.classList.add("dragging");
	startX = e.pageX;
	startScrollLeft = carousel.scrollLeft;
};

const dragging = (e) => {
	if (!isDragging) return;
	carousel.scrollLeft = startScrollLeft - (e.pageX - startX);
};

const dragStop = () => {
	isDragging = false;
	carousel.classList.remove("dragging");
};

const infiniteScroll = () => {

	if (carousel.scrollLeft === 0) {
		carousel.classList.add("no-transition");
		carousel.scrollLeft = carousel.scrollWidth - 2 * carousel.offsetWidth;
		carousel.classList.remove("no-transition");
	} else if (
		Math.ceil(carousel.scrollLeft) ===
		carousel.scrollWidth - carousel.offsetWidth
	) {
		carousel.classList.add("no-transition");
		carousel.scrollLeft = carousel.offsetWidth;
		carousel.classList.remove("no-transition");
	}


	clearTimeout(timeoutId);
	if (!wrapper.matches(":hover")) autoPlay();
};

const autoPlay = () => {
	if (window.innerWidth < 800 || !isAutoPlay) return;
	timeoutId = setTimeout(() => (carousel.scrollLeft += firstCardWidth), 2500);
};
autoPlay();

carousel.addEventListener("mousedown", dragStart);
carousel.addEventListener("mousemove", dragging);
document.addEventListener("mouseup", dragging);

</script>
</body>
</html>








